<?php// showScores.php //// fetches top 10 high scores from database //// set database data //$dbhost = "";$dbusername = "";$dbuserpassword = "";$dbname = "mostwanted";// connect to database //$link = mysql_connect ($dbhost, $dbusername, $dbuserpassword);if (!$link) {	echo "&result=noconnection";} else {	mysql_select_db($dbname);}// set query to call top 10 //$query = "SELECT * FROM highscore ORDER BY score DESC LIMIT 0, 10;";$result = mysql_query ($query);// init count		$count = 0;// fetch data and outputwhile ($query_data = mysql_fetch_array ($result)) {		$player = $query_data ["player"];		$score = $query_data ["score"];				// data outputted as variables of this form : player0=myname ... player9=yourname //						echo "&player$count=$player"; 		echo "&score$count=$score";					$count++;}// send extra data to flash //echo "&result=okay";echo "&rows=$count&";?>